INSERT INTO `wp_options` VALUES ('515', '_transient_dash_88ae138922fe95674369b1cb3d215a2b', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/11/wordpress-4-4-release-candidate/\'>WordPress 4.4 Release Candidate</a> <span class=\"rss-date\">25 November, 2015</span><div class=\"rssSummary\">The release candidate for WordPress 4.4 is now available. RC means we think we’re done, but with millions of users and thousands of plugins and themes, it’s possible we’ve missed something. We hope to ship WordPress 4.4 on Tuesday, December 8, but we need your help to get there. If you haven’t tested 4.4 yet, [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/wpweekly-episode-215-all-about-logos-with-andrew-bergeron\'>WPTavern: WPWeekly Episode 215 – All About Logos With Andrew Bergeron</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/12/george-lakoff/\'>Matt: George Lakoff</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/joost-de-valk-on-the-lessons-learned-after-releasing-wordpress-seo-3-0\'>WPTavern: Joost de Valk on the Lessons Learned After Releasing WordPress SEO 3.0</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/siteorigin-panels/\' class=\'dashboard-news-plugin-link\'>Page Builder by SiteOrigin</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=siteorigin-panels&amp;_wpnonce=7cc1f50777&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Page Builder by SiteOrigin\'>Install</a>)</span></li></ul></div>', 'no');
