INSERT INTO `wp_posts` VALUES ('1347', '1', '2015-12-04 01:50:54', '2015-12-04 01:50:54', '[gallery]\n\n<!--nextpage-->\n\nYou can use this page to test the Theme\'s handling of the[gallery]\n\nshortcode, including the <code>columns</code> parameter, from 1 to 10 columns. Themes are only required to support the default setting (3 columns), so this page is entirely optional.\n<h2>One Column</h2>\n[gallery columns=\"1\"]\n<h2>Two Columns</h2>\n[gallery columns=\"2\"]\n<h2>Three Columns</h2>\n[gallery columns=\"3\"]\n<h2>Four Columns</h2>\n[gallery columns=\"4\"]\n<h2>Five Columns</h2>\n[gallery columns=\"5\"]\n<h2>Six Columns</h2>\n[gallery columns=\"6\"]\n<h2>Seven Columns</h2>\n[gallery columns=\"7\"]\n<h2>Eight Columns</h2>\n[gallery columns=\"8\"]\n<h2>Nine Columns</h2>\n[gallery columns=\"9\"]\n<h2>Ten Columns</h2>\n[gallery columns=\"10\"]', 'Post Format: Gallery', '', 'inherit', 'closed', 'closed', '', '555-revision-v1', '', '', '2015-12-04 01:50:54', '2015-12-04 01:50:54', '', '555', 'http://cuong-wp4.local/555-revision-v1/', '0', 'revision', '', '0');
